function searchword(text, word)
{

    var x = 0, y=0;
    var w = word;

    for (i=0;i< text.length;i++)
        {
        if(text[i] == word[0])
            {
                for(j=i;j< i+word.length;j++)
                {
                    if(text[j]==word[j-i])
                    {
                        y++;
                    }
                    if (y==word.length)
                    {
                        x++;

                    }
                }
            y=0;
            }
        }
    if(x > 0)
    {
        return w
    }
    else
    {
        return 0
    }
}

function multicall2(list)
{
    var ultlist = [];
    var size = [];
    for(let i = 0;i<list.length;i++)
    {
        var semiult = [];
        var big = list[i];
        var take;
        for(let j=0;j<big.length;j++)
        {
            take = searchword(msg.payload.pages[0].text, big[j]);
            if (take == 0)
            {
                continue;
            }
            else
            {
                semiult.push(big[j]);
            }
        }
        ultlist.push(semiult);
        size.push(semiult.length);
    }
    return [ultlist,size];
}



